import { Component, Input } from '@angular/core';
import { UserComment } from '../../interfaces/INews';

@Component({
  selector: 'app-user-comment',
  templateUrl: './user-comment.component.html',
  styleUrls: ['./user-comment.component.scss']
})
export class UserCommentComponent {
  @Input() comments: UserComment[] = [];
}





<div class="mt-3" *ngFor="let comment of comments">
  <!-- Conteúdo do Comentário -->
  <p class="border p-3 rounded bg-light mb-2">{{ comment.comment }}</p>

  <!-- Detalhes do Usuário -->
  <small class="text-muted">Comentado por: {{ comment.userId }}</small>
  <hr>
</div>
