import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { FormGroup, FormBuilder, Validators } from '@angular/forms';
import { AuthenticationService } from '../../services/authentication.service';

import { User } from '../../interfaces/IUsuario';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss']
})
export class RegisterComponent implements OnInit {
  registerForm!: FormGroup;
  errorMessage: string = '';

  constructor(
    private authService: AuthenticationService,
    private router: Router,
    private formBuilder: FormBuilder
  ) {}

  ngOnInit(): void {
    this.registerForm = this.formBuilder.group({
      username: ['', Validators.required], // adiciona isto
      name: ['', Validators.required],
      password: ['', Validators.required],
      email: ['', [Validators.required, Validators.email]],
      birthday: ['', Validators.required],
      terms: [false, Validators.requiredTrue]
    });
  }

  register(): void {
    if (this.registerForm.valid) {
      const user: User = {
        id: null, // adicionando propriedade id
        username: this.registerForm.value.username,
        name: this.registerForm.value.name,
        password: this.registerForm.value.password,
        email: this.registerForm.value.email,
        birthday: this.registerForm.value.birthday,
      };

      this.authService.register(user).subscribe(
        () => {
          this.router.navigate(['/home']);
        },
        error => {
          this.errorMessage = error;
        }
      );
    }
  }
}
<main>
  <div class="container-sm py-5">
    <div class="row justify-content-center">
      <div class="col-md-8">
        <div class="card bg-light shadow-lg">
          <div class="card-body">
            <h3 class="card-title text-center mb-4">Cadastro</h3>
            <form [formGroup]="registerForm" (ngSubmit)="register()">
              <div class="row">
                <div class="col-md-6 form-group">
                  <label for="username">Usuário</label>
                  <input type="text" class="form-control" id="username" formControlName="username" placeholder="Usuário">
                  <div class="text-danger" *ngIf="registerForm.controls['username']?.invalid && (registerForm.controls['username']?.dirty || registerForm.controls['username']?.touched)">
                    O campo usuário é obrigatório
                  </div>
                </div>
                <div class="col-md-6 form-group">
                  <label for="name">Nome</label>
                  <input type="text" class="form-control" id="name" formControlName="name" placeholder="Nome">
                  <div class="text-danger" *ngIf="registerForm.controls['name']?.invalid && (registerForm.controls['name']?.dirty || registerForm.controls['name']?.touched)">
                    O campo nome é obrigatório
                  </div>
                </div>
                <div class="col-md-6 form-group">
                  <label for="email">E-mail</label>
                  <input type="email" class="form-control" id="email" formControlName="email" placeholder="E-mail">
                  <div class="text-danger" *ngIf="registerForm.controls['email']?.invalid && (registerForm.controls['email']?.dirty || registerForm.controls['email']?.touched)">
                    O campo e-mail é obrigatório
                  </div>
                </div>
                <div class="col-md-6 form-group">
                  <label for="birthday">Data de nascimento</label>
                  <input type="date" class="form-control" id="birthday" formControlName="birthday">
                  <div class="text-danger" *ngIf="registerForm.controls['birthday']?.invalid && (registerForm.controls['birthday']?.dirty || registerForm.controls['birthday']?.touched)">
                    A data de nascimento é obrigatória
                  </div>
                  <div class="text-danger" *ngIf="registerForm.controls['birthday'].errors?.['ageInvalid']">
                    Você precisa ter pelo menos 18 anos para se registrar.
                  </div>
                </div>
                <div class="col-md-6 form-group">
                  <label for="password">Senha</label>
                  <input type="password" class="form-control" id="password" formControlName="password" placeholder="Senha">
                  <div class="text-danger" *ngIf="registerForm.controls['password']?.invalid && (registerForm.controls['password']?.dirty || registerForm.controls['password']?.touched)">
                    O campo senha é obrigatório
                  </div>
                </div>
                <div class="col-md-6 form-group">
                  <label for="confirmPassword">Confirmar Senha</label>
                  <input type="password" class="form-control" id="confirmPassword" formControlName="confirmPassword" placeholder="Confirmar Senha">
                  <!-- Pode-se adicionar validação para conferir se a senha e a confirmação são iguais -->
                </div>
              </div>
              <div class="form-group form-check mt-3">
                <input type="checkbox" class="form-check-input" id="terms" formControlName="terms">
                <label class="form-check-label" for="terms">Eu aceito os <a href="#">termos de uso</a></label>
                <div class="text-danger" *ngIf="registerForm.controls['terms']?.invalid && (registerForm.controls['terms']?.dirty || registerForm.controls['terms']?.touched)">
                    Aceitar os termos de uso é obrigatório
                </div>
              </div>
              <div class="text-center mt-4">
                <button type="submit" class="btn btn-primary btn-lg">Registrar</button>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  </div>
</main>
