import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, ParamMap, Router } from '@angular/router';
import { switchMap } from 'rxjs/operators';
import { of, Observable } from 'rxjs';
import { Product } from '../model/product.model';
import { EcommerceService } from '../../services/ecommerce.service';
import { CartService } from '../../services/cart.service';
import { AuthenticationService, User } from '../../services/authentication.service'; 

@Component({
  selector: 'app-product-details',
  templateUrl: './product-details.component.html',
  styleUrls: ['./product-details.component.scss']
})
export class ProductDetailsComponent implements OnInit {
  product: Product | undefined;
  categories: any[] = [];  // Adicione esta linha para armazenar as categorias
  currentUser: User | null = null;
  loading = true;

  constructor(
    private route: ActivatedRoute,
    private ecommerceService: EcommerceService,
    private cartService: CartService,
    private authService: AuthenticationService,
    private router: Router
  ) {}

  ngOnInit(): void {
    this.route.paramMap.pipe(
      switchMap((params: ParamMap) =>
        this.ecommerceService.getProductDetails(Number(params.get('id')))
      )
    ).subscribe(
      (product: Product) => {
        this.product = product;
        setTimeout(() => {
          this.loading = false;
        }, 300);
      },
      (error) => {
        console.error('Error loading product details:', error);
      }
    );

    // Carregue as categorias aqui, como você fez em ProductListComponent
    this.ecommerceService.getCategories().subscribe((categories: any[]) => {
      this.categories = categories;
    });

    this.currentUser = this.authService.getCurrentUser();
  }

  addToCart(product: Product) {
    if (this.currentUser) {
      this.cartService.addToCart(product, this.currentUser.id);
    }
    return of(null);
  }

  buyNow(product: Product) {
    this.addToCart(product).subscribe(() => {
      this.router.navigate(['/checkout']);
    });
  }

  // Copie esta função do ProductListComponent
  getCategoryNameById(id: number): string {
    const category = this.categories.find(cat => cat.id === id);
    return category ? category.name : 'Desconhecido';
  }
}










<main>
  <div *ngIf="loading" class="spinner-overlay">
    <div class="spinner-container">
      <div class="spinner-border text-info" role="status">
        <span class="visually-hidden">Loading...</span>
      </div>
    </div>
  </div>
  <div class="container py-5">
    <div class="row">
      <div class="col-lg-8 col-md-10 mx-auto" *ngIf="product">
        <div class="card product-detail-card border-0 shadow" >
          <div class="row g-0">
            <div class="col-md-6">
              <!-- Image wrapper -->
              <div class="product-image-wrapper py-5">
                <img [src]="product.url" class="img-fluid rounded-start product-image" alt="Product Image">
              </div>

              <!-- Buttons below the image -->
              <div class="mt-4 d-flex flex-column align-items-center ">
                <p class="card-text mb-2"><span class="badge bg-primary">{{ getCategoryNameById(product.categoria) }}</span></p>
                <h5 class="card-text mt-3 mb-3">Preço: <span class="text-success">{{ product.preco }}</span></h5>
              </div>
              <div class="d-flex justify-content-center mt-2">
                <a (click)="addToCart(product)" class="btn btn-secondary"><i class="bi bi-cart-plus"></i> Add Carrinho</a>
                <a (click)="buyNow(product)" class="btn btn-success ms-2"><i class="bi bi-cash"></i> Comprar</a>
              </div>
            </div> <!-- Fechamento da tag <div> que estava faltando -->

            <div class="col-md-6">
              <div class="card-body">
                <h2 class="card-title">{{ product.nomeProduto }}</h2>
                <p class="card-text text-justify">{{ product.descricao }}</p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</main>
