import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, ParamMap } from '@angular/router';
import { switchMap } from 'rxjs/operators';
import { Product } from '../model/product.model';
import { EcommerceService } from '../../services/ecommerce.service';
import { CartService } from '../../services/cart.service';
import { AuthenticationService, User } from '../../services/authentication.service'; 

@Component({
  selector: 'app-product-category-details',
  templateUrl: './product-category-details.component.html',
  styleUrls: ['./product-category-details.component.scss']
})
export class ProductCategoryDetailsComponent implements OnInit {
  products: Product[] = [];

  constructor(
    private route: ActivatedRoute,
    private ecommerceService: EcommerceService
  ) {}

  ngOnInit(): void {
    this.route.paramMap.pipe(
      switchMap((params: ParamMap) => {
        const category = params.get('category') ?? ''; // Use uma string vazia como valor padrão se category for null
        return this.ecommerceService.getProductsByCategory(category);
      })
    ).subscribe(
      (products: Product[]) => {
        this.products = products;
      },
      (error) => {
        // Lógica de tratamento de erro, se necessário
        console.error('Error loading products by category:', error);
      }
    );
  }

}



<section class="container py-5">
  <div class="row">
    <div class="col-lg-3 col-md-3 col-sm-6 mb-4" *ngFor="let product of products">
      <div class="card h-100 shadow border-0">
        <img [src]="product.url" class="card-img-top my-4" alt="Product Image">
        <div class="card-body">
          <h5 class="card-title">{{ product.nomeProduto }}</h5>
          <p class="card-text">{{ product.descricao }}</p>
          <h6>Preço: {{ product.preco }}</h6>
          <p class="card-text">Categoria: {{ product.categoria }}</p>
        </div>
        <div class="card-footer d-flex justify-content-between">
          <a [routerLink]="['/product-details', product.id]" class="btn btn-primary btn-sm my-2 small-text mr-2">Detalhes</a>
          <a  class="btn btn-secondary btn-sm my-2 small-text mr-2">Add carrinho</a>
          <a href="#" class="btn btn-success btn-sm my-2 small-text">Comprar</a>
        </div>
      </div>
    </div>
  </div>
</section>
