import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { NewsService } from '../../services/news.service';
import { News, UserComment } from '../../interfaces/INews';

@Component({
  selector: 'app-news-detail',
  templateUrl: './news-details.component.html',
  styleUrls: ['./news-details.component.scss']
})
export class NewsDetailComponent implements OnInit {

  newsItem: News = {
    titulo: '', url: '', conteudo: '',
    id: 0
  };
  comments: UserComment[] = [];
  loading = true;

  constructor(
    private route: ActivatedRoute,
    private newsService: NewsService
  ) {}

  ngOnInit(): void {
    const newsId = Number(this.route.snapshot.params['id']); // Converta para Number

    // Chamada para obter a notícia pelo ID
    this.newsService.getNewsById(newsId).subscribe((news: News) => {
      console.log("Notícia recebida:", news); // Adicione esta linha
      this.newsItem = news;
      setTimeout(() => {
        this.loading = false;
      }, 300);
    });

    // Chamada para obter os comentários relacionados à notícia
    this.newsService.getUserCommentsForNews(newsId).subscribe((comments: UserComment[]) => {
      console.log("Comentários recebidos:", comments); // Adicione esta linha
      this.comments = comments;
    });
  }
}



<section class="py-5">
  <div *ngIf="loading" class="spinner-overlay">
    <div class="spinner-container">
      <div class="spinner-border text-info" role="status">
        <span class="visually-hidden">Loading...</span>
      </div>
    </div>
  </div>
  <div class="container2">
    <div class="row">
      <ng-container *ngIf="newsItem.titulo">
        <!-- Imagem da Notícia -->
        <div class="float-lg-left mr-lg-4 mb-4">
          <img [src]="newsItem.url" alt="{{ newsItem.titulo }}" class="img-fluid rounded news-detail-image">
        </div>
        <!-- Título e Conteúdo da Notícia -->
        <div>
          <h2 class="mb-4 display-4 text-justify">{{ newsItem.titulo }}</h2>
          <p class="text-justify">{{ newsItem.conteudo }}</p>
        </div>
        <!-- Clearfix para limpar o float -->
        <div class="clearfix"></div>
      </ng-container>
    </div>
    <!-- Seção de Comentários -->
    <div class="row mt-5">
      <div class="col-lg-8 mx-auto">
        <h4 class="text-justify">Comentários</h4>
        <app-user-comment [comments]="comments"></app-user-comment>
      </div>
    </div>
  </div>
</section>
